                AssertEx.EqualTolerance(1, parsed.CubicCentimeters, CubicCentimetersTolerance);
                Assert.Equal(SectionModulusUnit.CubicCentimeter, parsed.Unit);
            } catch (AmbiguousUnitParseException) { /* Some units have the same abbreviations */ }

            try
            {
                var parsed = SectionModulus.Parse("1 ft³", CultureInfo.GetCultureInfo("en-US"));
                AssertEx.EqualTolerance(1, parsed.CubicFeet, CubicFeetTolerance);
                Assert.Equal(SectionModulusUnit.CubicFoot, parsed.Unit);
            } catch (AmbiguousUnitParseException) { /* Some units have the same abbreviations */ }

            try
            {
                var parsed = SectionModulus.Parse("1 in³", CultureInfo.GetCultureInfo("en-US"));
                AssertEx.EqualTolerance(1, parsed.CubicInches, CubicInchesTolerance);
                Assert.Equal(SectionModulusUnit.CubicInch, parsed.Unit);
            } catch (AmbiguousUnitParseException) { /* Some units have the same abbreviations */ }

            try
            {
                var parsed = SectionModulus.Parse("1 m³", CultureInfo.GetCultureInfo("en-US"));
                AssertEx.EqualTolerance(1, parsed.CubicMeters, CubicMetersTolerance);
                Assert.Equal(SectionModulusUnit.CubicMeter, parsed.Unit);
            } catch (AmbiguousUnitParseException) { /* Some units have the same abbreviations */ }

            try
            {
                var parsed = SectionModulus.Parse("1 mm³", CultureInfo.GetCultureInfo("en-US"));
                AssertEx.EqualTolerance(1, parsed.CubicMillimeters, CubicMillimetersTolerance);
                Assert.Equal(SectionModulusUnit.CubicMillimeter, parsed.Unit);
            } catch (AmbiguousUnitParseException) { /* Some units have the same abbreviations */ }

        }

