        [Theory]
        [InlineData(1, SectionModulusUnit.CubicMeter, 1, SectionModulusUnit.CubicMeter, true)]  // Same value and unit.
        [InlineData(1, SectionModulusUnit.CubicMeter, 2, SectionModulusUnit.CubicMeter, false)] // Different value.
        [InlineData(2, SectionModulusUnit.CubicMeter, 1, SectionModulusUnit.CubicCentimeter, false)] // Different value and unit.
        [InlineData(1, SectionModulusUnit.CubicMeter, 1, SectionModulusUnit.CubicCentimeter, false)] // Different unit.
        public void Equals_ReturnsTrue_IfValueAndUnitAreEqual(double valueA, SectionModulusUnit unitA, double valueB, SectionModulusUnit unitB, bool expectEqual)
        {
            var a = new SectionModulus(valueA, unitA);
            var b = new SectionModulus(valueB, unitB);

            // Operator overloads.
            Assert.Equal(expectEqual, a == b);
            Assert.Equal(expectEqual, b == a);
            Assert.Equal(!expectEqual, a != b);
            Assert.Equal(!expectEqual, b != a);

            // IEquatable<T>
            Assert.Equal(expectEqual, a.Equals(b));
            Assert.Equal(expectEqual, b.Equals(a));

            // IEquatable
            Assert.Equal(expectEqual, a.Equals((object)b));
            Assert.Equal(expectEqual, b.Equals((object)a));
        }

        [Fact]
        public void Equals_Null_ReturnsFalse()
        {
            var a = SectionModulus.Zero;

            Assert.False(a.Equals((object)null));

            // "The result of the expression is always 'false'..."
            #pragma warning disable CS8073
            Assert.False(a == null);
            Assert.False(null == a);
            Assert.True(a != null);
            Assert.True(null != a);
            #pragma warning restore CS8073
        }

        [Fact]
        public void Equals_RelativeTolerance_IsImplemented()
        {
            var v = SectionModulus.FromCubicMeters(1);
            Assert.True(v.Equals(SectionModulus.FromCubicMeters(1), CubicMetersTolerance, ComparisonType.Relative));
            Assert.False(v.Equals(SectionModulus.Zero, CubicMetersTolerance, ComparisonType.Relative));
            Assert.True(SectionModulus.FromCubicMeters(100).Equals(SectionModulus.FromCubicMeters(120), 0.3, ComparisonType.Relative));
            Assert.False(SectionModulus.FromCubicMeters(100).Equals(SectionModulus.FromCubicMeters(120), 0.1, ComparisonType.Relative));
        }

