        [Fact]
        public void ToString_ReturnsValueAndUnitAbbreviationInCurrentCulture()
        {
            var prevCulture = Thread.CurrentThread.CurrentCulture;
            Thread.CurrentThread.CurrentCulture = CultureInfo.GetCultureInfo("en-US");
            try {
                Assert.Equal("1 cm³", new SectionModulus(1, SectionModulusUnit.CubicCentimeter).ToString());
                Assert.Equal("1 ft³", new SectionModulus(1, SectionModulusUnit.CubicFoot).ToString());
                Assert.Equal("1 in³", new SectionModulus(1, SectionModulusUnit.CubicInch).ToString());
                Assert.Equal("1 m³", new SectionModulus(1, SectionModulusUnit.CubicMeter).ToString());
                Assert.Equal("1 mm³", new SectionModulus(1, SectionModulusUnit.CubicMillimeter).ToString());
            }
            finally
            {
                Thread.CurrentThread.CurrentCulture = prevCulture;
            }
        }

