            finally
            {
                CultureInfo.CurrentCulture = oldCulture;
            }
        }

        [Fact]
        public void ToString_SFormatAndCulture_FormatsNumberWithGivenDigitsAfterRadixForGivenCulture()
        {
            var culture = CultureInfo.InvariantCulture;
            Assert.Equal("0.1 m³", new SectionModulus(0.123456, SectionModulusUnit.CubicMeter).ToString("s1", culture));
            Assert.Equal("0.12 m³", new SectionModulus(0.123456, SectionModulusUnit.CubicMeter).ToString("s2", culture));
            Assert.Equal("0.123 m³", new SectionModulus(0.123456, SectionModulusUnit.CubicMeter).ToString("s3", culture));
            Assert.Equal("0.1235 m³", new SectionModulus(0.123456, SectionModulusUnit.CubicMeter).ToString("s4", culture));
        }

