        [Fact]
        public void ConversionRoundTrip()
        {
            Strain ratio = Strain.FromRatio(1);
            AssertEx.EqualTolerance(1, Strain.FromMicroStrain(ratio.MicroStrain).Ratio, MicroStrainTolerance);
            AssertEx.EqualTolerance(1, Strain.FromMilliStrain(ratio.MilliStrain).Ratio, MilliStrainTolerance);
            AssertEx.EqualTolerance(1, Strain.FromPercent(ratio.Percent).Ratio, PercentTolerance);
            AssertEx.EqualTolerance(1, Strain.FromRatio(ratio.Ratio).Ratio, RatioTolerance);
        }

        [Fact]
        public void ArithmeticOperators()
        {
            Strain v = Strain.FromRatio(1);
            AssertEx.EqualTolerance(-1, -v.Ratio, RatioTolerance);
            AssertEx.EqualTolerance(2, (Strain.FromRatio(3)-v).Ratio, RatioTolerance);
            AssertEx.EqualTolerance(2, (v + v).Ratio, RatioTolerance);
            AssertEx.EqualTolerance(10, (v*10).Ratio, RatioTolerance);
            AssertEx.EqualTolerance(10, (10*v).Ratio, RatioTolerance);
            AssertEx.EqualTolerance(2, (Strain.FromRatio(10)/5).Ratio, RatioTolerance);
            AssertEx.EqualTolerance(2, Strain.FromRatio(10)/Strain.FromRatio(5), RatioTolerance);
        }

        [Fact]
        public void ComparisonOperators()
        {
            Strain oneRatio = Strain.FromRatio(1);
            Strain twoRatio = Strain.FromRatio(2);

            Assert.True(oneRatio < twoRatio);
            Assert.True(oneRatio <= twoRatio);
            Assert.True(twoRatio > oneRatio);
            Assert.True(twoRatio >= oneRatio);

            Assert.False(oneRatio > twoRatio);
            Assert.False(oneRatio >= twoRatio);
            Assert.False(twoRatio < oneRatio);
            Assert.False(twoRatio <= oneRatio);
        }

