        [Theory]
        [InlineData(1, StrainUnit.Ratio, 1, StrainUnit.Ratio, true)]  // Same value and unit.
        [InlineData(1, StrainUnit.Ratio, 2, StrainUnit.Ratio, false)] // Different value.
        [InlineData(2, StrainUnit.Ratio, 1, StrainUnit.MicroStrain, false)] // Different value and unit.
        [InlineData(1, StrainUnit.Ratio, 1, StrainUnit.MicroStrain, false)] // Different unit.
        public void Equals_ReturnsTrue_IfValueAndUnitAreEqual(double valueA, StrainUnit unitA, double valueB, StrainUnit unitB, bool expectEqual)
        {
            var a = new Strain(valueA, unitA);
            var b = new Strain(valueB, unitB);

            // Operator overloads.
            Assert.Equal(expectEqual, a == b);
            Assert.Equal(expectEqual, b == a);
            Assert.Equal(!expectEqual, a != b);
            Assert.Equal(!expectEqual, b != a);

            // IEquatable<T>
            Assert.Equal(expectEqual, a.Equals(b));
            Assert.Equal(expectEqual, b.Equals(a));

            // IEquatable
            Assert.Equal(expectEqual, a.Equals((object)b));
            Assert.Equal(expectEqual, b.Equals((object)a));
        }

        [Fact]
        public void Equals_Null_ReturnsFalse()
        {
            var a = Strain.Zero;

            Assert.False(a.Equals((object)null));

            // "The result of the expression is always 'false'..."
            #pragma warning disable CS8073
            Assert.False(a == null);
            Assert.False(null == a);
            Assert.True(a != null);
            Assert.True(null != a);
            #pragma warning restore CS8073
        }

        [Fact]
        public void Equals_RelativeTolerance_IsImplemented()
        {
            var v = Strain.FromRatio(1);
            Assert.True(v.Equals(Strain.FromRatio(1), RatioTolerance, ComparisonType.Relative));
            Assert.False(v.Equals(Strain.Zero, RatioTolerance, ComparisonType.Relative));
            Assert.True(Strain.FromRatio(100).Equals(Strain.FromRatio(120), 0.3, ComparisonType.Relative));
            Assert.False(Strain.FromRatio(100).Equals(Strain.FromRatio(120), 0.1, ComparisonType.Relative));
        }

