        [Fact]
        public void ToString_SFormat_FormatsNumberWithGivenDigitsAfterRadixForCurrentCulture()
        {
            var oldCulture = CultureInfo.CurrentCulture;
            try
            {
                CultureInfo.CurrentCulture = CultureInfo.InvariantCulture;
                Assert.Equal("0.1 ε", new Strain(0.123456, StrainUnit.Ratio).ToString("s1"));
                Assert.Equal("0.12 ε", new Strain(0.123456, StrainUnit.Ratio).ToString("s2"));
                Assert.Equal("0.123 ε", new Strain(0.123456, StrainUnit.Ratio).ToString("s3"));
                Assert.Equal("0.1235 ε", new Strain(0.123456, StrainUnit.Ratio).ToString("s4"));
            }
            finally
