        /// <summary>
        ///     Convert to the unit representation <paramref name="unit" />.
        /// </summary>
        /// <returns>Value converted to the specified unit.</returns>
        public double As(CurvatureUnit unit)
        {
            if (Unit == unit)
                return Value;

            return ToUnit(unit).Value;
        }

        /// <inheritdoc cref="IQuantity.As(UnitSystem)"/>
        public double As(UnitSystem unitSystem)
        {
            if (unitSystem is null)
                throw new ArgumentNullException(nameof(unitSystem));

            var unitInfos = Info.GetUnitInfosFor(unitSystem.BaseUnits);

            var firstUnitInfo = unitInfos.FirstOrDefault();
            if (firstUnitInfo == null)
                throw new ArgumentException("No units were found for the given UnitSystem.", nameof(unitSystem));

            return As(firstUnitInfo.Value);
        }

        /// <inheritdoc />
        double IQuantity.As(Enum unit)
        {
            if (!(unit is CurvatureUnit typedUnit))
                throw new ArgumentException($"The given unit is of type {unit.GetType()}. Only {typeof(CurvatureUnit)} is supported.", nameof(unit));

            return As(typedUnit);
        }

        /// <summary>
        ///     Converts this Curvature to another Curvature with the unit representation <paramref name="unit" />.
        /// </summary>
        /// <param name="unit">The unit to convert to.</param>
        /// <returns>A Curvature with the specified unit.</returns>
        public Curvature ToUnit(CurvatureUnit unit)
        {
            return ToUnit(unit, DefaultConversionFunctions);
        }

        /// <summary>
        ///     Converts this <see cref="Curvature"/> to another <see cref="Curvature"/> using the given <paramref name="unitConverter"/> with the unit representation <paramref name="unit" />.
        /// </summary>
        /// <param name="unit">The unit to convert to.</param>
        /// <param name="unitConverter">The <see cref="UnitConverter"/> to use for the conversion.</param>
        /// <returns>A Curvature with the specified unit.</returns>
        public Curvature ToUnit(CurvatureUnit unit, UnitConverter unitConverter)
        {
            if (TryToUnit(unit, out var converted))
            {
                // Try to convert using the auto-generated conversion methods.
                return converted!.Value;
            }
            else if (unitConverter.TryGetConversionFunction((typeof(Curvature), Unit, typeof(Curvature), unit), out var conversionFunction))
            {
                // See if the unit converter has an extensibility conversion registered.
                return (Curvature)conversionFunction(this);
            }
            else if (Unit != BaseUnit)
            {
                // Conversion to requested unit NOT found. Try to convert to BaseUnit, and then from BaseUnit to requested unit.
                var inBaseUnits = ToUnit(BaseUnit);
                return inBaseUnits.ToUnit(unit);
            }
            else
            {
                // No possible conversion
                throw new NotImplementedException($"Can not convert {Unit} to {unit}.");
            }
        }

        /// <summary>
        ///     Attempts to convert this <see cref="Curvature"/> to another <see cref="Curvature"/> with the unit representation <paramref name="unit" />.
        /// </summary>
        /// <param name="unit">The unit to convert to.</param>
        /// <param name="converted">The converted <see cref="Curvature"/> in <paramref name="unit"/>, if successful.</param>
        /// <returns>True if successful, otherwise false.</returns>
        private bool TryToUnit(CurvatureUnit unit, [NotNullWhen(true)] out Curvature? converted)
        {
            if (Unit == unit)
            {
                converted = this;
                return true;
            }

            Curvature? convertedOrNull = (Unit, unit) switch
            {
                // CurvatureUnit -> BaseUnit
                (CurvatureUnit.PerCentimeter, CurvatureUnit.PerMeter) => new Curvature(_value*1e2, CurvatureUnit.PerMeter),
                (CurvatureUnit.PerFoot, CurvatureUnit.PerMeter) => new Curvature(_value/0.3048, CurvatureUnit.PerMeter),
                (CurvatureUnit.PerInch, CurvatureUnit.PerMeter) => new Curvature(_value/2.54e-2, CurvatureUnit.PerMeter),
                (CurvatureUnit.PerMillimeter, CurvatureUnit.PerMeter) => new Curvature(_value*1e3, CurvatureUnit.PerMeter),

                // BaseUnit -> CurvatureUnit
                (CurvatureUnit.PerMeter, CurvatureUnit.PerCentimeter) => new Curvature(_value/1e2, CurvatureUnit.PerCentimeter),
                (CurvatureUnit.PerMeter, CurvatureUnit.PerFoot) => new Curvature(_value*0.3048, CurvatureUnit.PerFoot),
                (CurvatureUnit.PerMeter, CurvatureUnit.PerInch) => new Curvature(_value*2.54e-2, CurvatureUnit.PerInch),
                (CurvatureUnit.PerMeter, CurvatureUnit.PerMillimeter) => new Curvature(_value/1e3, CurvatureUnit.PerMillimeter),

                _ => null
            };

            if (convertedOrNull is null)
            {
                converted = default;
                return false;
            }

            converted = convertedOrNull.Value;
            return true;
        }

        /// <inheritdoc />
        IQuantity IQuantity.ToUnit(Enum unit)
        {
            if (!(unit is CurvatureUnit typedUnit))
                throw new ArgumentException($"The given unit is of type {unit.GetType()}. Only {typeof(CurvatureUnit)} is supported.", nameof(unit));

            return ToUnit(typedUnit, DefaultConversionFunctions);
        }

        /// <inheritdoc cref="IQuantity.ToUnit(UnitSystem)"/>
        public Curvature ToUnit(UnitSystem unitSystem)
        {
            if (unitSystem is null)
                throw new ArgumentNullException(nameof(unitSystem));

            var unitInfos = Info.GetUnitInfosFor(unitSystem.BaseUnits);

            var firstUnitInfo = unitInfos.FirstOrDefault();
            if (firstUnitInfo == null)
                throw new ArgumentException("No units were found for the given UnitSystem.", nameof(unitSystem));

            return ToUnit(firstUnitInfo.Value);
        }

        /// <inheritdoc />
        IQuantity IQuantity.ToUnit(UnitSystem unitSystem) => ToUnit(unitSystem);

        /// <inheritdoc />
        IQuantity<CurvatureUnit> IQuantity<CurvatureUnit>.ToUnit(CurvatureUnit unit) => ToUnit(unit);

        /// <inheritdoc />
        IQuantity<CurvatureUnit> IQuantity<CurvatureUnit>.ToUnit(UnitSystem unitSystem) => ToUnit(unitSystem);

