        /// <inheritdoc cref="IQuantity.As(UnitSystem)"/>
        public double As(UnitSystem unitSystem)
        {
            if (unitSystem is null)
                throw new ArgumentNullException(nameof(unitSystem));

            var unitInfos = Info.GetUnitInfosFor(unitSystem.BaseUnits);

            var firstUnitInfo = unitInfos.FirstOrDefault();
            if (firstUnitInfo == null)
                throw new ArgumentException("No units were found for the given UnitSystem.", nameof(unitSystem));

            return As(firstUnitInfo.Value);
        }

        /// <inheritdoc />
        double IQuantity.As(Enum unit)
        {
            if (!(unit is StrainUnit unitAsStrainUnit))
                throw new ArgumentException($"The given unit is of type {unit.GetType()}. Only {typeof(StrainUnit)} is supported.", nameof(unit));

            return As(unitAsStrainUnit);
        }

        /// <summary>
        ///     Converts this Strain to another Strain with the unit representation <paramref name="unit" />.
        /// </summary>
        /// <param name="unit">The unit to convert to.</param>
        /// <returns>A Strain with the specified unit.</returns>
        public Strain ToUnit(StrainUnit unit)
        {
            return ToUnit(unit, DefaultConversionFunctions);
        }

        /// <summary>
        ///     Converts this <see cref="Strain"/> to another <see cref="Strain"/> using the given <paramref name="unitConverter"/> with the unit representation <paramref name="unit" />.
        /// </summary>
        /// <param name="unit">The unit to convert to.</param>
        /// <param name="unitConverter">The <see cref="UnitConverter"/> to use for the conversion.</param>
        /// <returns>A Strain with the specified unit.</returns>
        public Strain ToUnit(StrainUnit unit, UnitConverter unitConverter)
        {
            if (TryToUnit(unit, out var converted))
            {
                // Try to convert using the auto-generated conversion methods.
                return converted!.Value;
            }
            else if (unitConverter.TryGetConversionFunction((typeof(Strain), Unit, typeof(Strain), unit), out var conversionFunction))
            {
                // See if the unit converter has an extensibility conversion registered.
                return (Strain)conversionFunction(this);
            }
            else if (Unit != BaseUnit)
            {
                // Conversion to requested unit NOT found. Try to convert to BaseUnit, and then from BaseUnit to requested unit.
                var inBaseUnits = ToUnit(BaseUnit);
                return inBaseUnits.ToUnit(unit);
            }
            else
            {
                // No possible conversion
                throw new NotImplementedException($"Can not convert {Unit} to {unit}.");
            }
        }

        /// <summary>
        ///     Attempts to convert this <see cref="Strain"/> to another <see cref="Strain"/> with the unit representation <paramref name="unit" />.
        /// </summary>
        /// <param name="unit">The unit to convert to.</param>
        /// <param name="converted">The converted <see cref="Strain"/> in <paramref name="unit"/>, if successful.</param>
        /// <returns>True if successful, otherwise false.</returns>
        private bool TryToUnit(StrainUnit unit, out Strain? converted)
        {
            if (Unit == unit)
            {
                converted = this;
                return true;
            }

            converted = (Unit, unit) switch
            {
                // StrainUnit -> BaseUnit
                (StrainUnit.MicroStrain, StrainUnit.Ratio) => new Strain(_value*1e-6, StrainUnit.Ratio),
                (StrainUnit.MilliStrain, StrainUnit.Ratio) => new Strain(_value*1e-3, StrainUnit.Ratio),
                (StrainUnit.Percent, StrainUnit.Ratio) => new Strain(_value/1e2, StrainUnit.Ratio),

                // BaseUnit -> StrainUnit
                (StrainUnit.Ratio, StrainUnit.MicroStrain) => new Strain(_value*1e6, StrainUnit.MicroStrain),
                (StrainUnit.Ratio, StrainUnit.MilliStrain) => new Strain(_value*1e3, StrainUnit.MilliStrain),
                (StrainUnit.Ratio, StrainUnit.Percent) => new Strain(_value*1e2, StrainUnit.Percent),

                _ => null!
            };

            return converted != null;
        }

        /// <inheritdoc />
        IQuantity IQuantity.ToUnit(Enum unit)
        {
            if (!(unit is StrainUnit unitAsStrainUnit))
                throw new ArgumentException($"The given unit is of type {unit.GetType()}. Only {typeof(StrainUnit)} is supported.", nameof(unit));

            return ToUnit(unitAsStrainUnit, DefaultConversionFunctions);
        }

        /// <inheritdoc cref="IQuantity.ToUnit(UnitSystem)"/>
        public Strain ToUnit(UnitSystem unitSystem)
        {
            if (unitSystem is null)
                throw new ArgumentNullException(nameof(unitSystem));

            var unitInfos = Info.GetUnitInfosFor(unitSystem.BaseUnits);

            var firstUnitInfo = unitInfos.FirstOrDefault();
            if (firstUnitInfo == null)
                throw new ArgumentException("No units were found for the given UnitSystem.", nameof(unitSystem));

            return ToUnit(firstUnitInfo.Value);
        }

        /// <inheritdoc />
        IQuantity IQuantity.ToUnit(UnitSystem unitSystem) => ToUnit(unitSystem);

        /// <inheritdoc />
        IQuantity<StrainUnit> IQuantity<StrainUnit>.ToUnit(StrainUnit unit) => ToUnit(unit);

        /// <inheritdoc />
        IQuantity<StrainUnit> IQuantity<StrainUnit>.ToUnit(UnitSystem unitSystem) => ToUnit(unitSystem);

